import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { GoComment, GoHeart } from "react-icons/go";
import { IoIosCloseCircleOutline } from "react-icons/io";
import { album_arr, ui_nav } from '../../../actions';

import './index.css';

import axios from 'axios';
import { URL } from '../../../const';

function BodyAlbum () {
    const dispatch = useDispatch();
    let album = useSelector(state => state.album);
    let acc = useSelector(state => state.acc);

    function One_del (e) {
        axios.post(URL + 'pic_del', {
            pic: e,
            user: acc.user
        })
        .then(res => {
            if (res.data) {
                alert('Successfully Deleted !');
                _handleAlbum();
            }
            else {
                alert('You are not allowed to delete this pic !');
            }
        })
    }

    function _handleAlbum() {
        axios.post(URL + 'picture', {})
        .then(res => {
            dispatch(album_arr(res.data));
            dispatch(ui_nav(2));
        });
    }

    function _like(e) {
        if (acc.user !== '') {
            axios.post(URL + 'like', {
                id: acc.user,
                pic: e
            })
            .then(res => {
                _handleAlbum();
            })
        }
        else {
            alert('Log in first !');
            dispatch(ui_nav(3));
        }
    }

    function _comment(e) {
        axios.post(URL + 'view_comment', {
            pic: e
        })
    }

    return (
        <div className="body-album">
            <div className="body-album-part">
                { album.arr.map((each, index) => 
                    <div className="body-album-each" key={index} >
                        <div className="body-album-pic" style={{ backgroundImage: 'url(\'data:image/jpeg;base64,' + each.temp + '\')' }} />
                        <GoHeart className="body-album-ft-1" onClick={() => _like(each.pic)}/>
                        <GoComment className="body-album-ft-2" onClick={() => _comment(each.pic)} />
                        <div className="body-album-postid">{each.name}</div>
                        <IoIosCloseCircleOutline className="body-album-ft2" onClick={() => One_del(each.pic)} />
                        <div className="count_like">{each.count} Likes</div>
                        <div className="count_comment">0 Comments</div>
                        {each.display === 0 ? <div className="body-album-comment">{each.explain}</div> : ''}
                    </div>
                )} 
            </div>
        </div>
    );
}

export default BodyAlbum;
