import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { GoComment, GoHeart } from "react-icons/go";
import { IoIosCloseCircleOutline } from "react-icons/io";
import { album_arr, album_comment, ui_nav } from '../../../actions';
import { IoIosAddCircleOutline } from "react-icons/io";


import './index.css';

import axios from 'axios';
import { URL } from '../../../const';

function BodyAlbum () {
    const dispatch = useDispatch();
    let album = useSelector(state => state.album);
    let acc = useSelector(state => state.acc);

    function One_del (e) {
        axios.post(URL + 'pic_del', {
            pic: e,
            user: acc.user
        })
        .then(res => {
            if (res.data) {
                alert('Successfully Deleted !');
                _handleAlbum();
            }
            else {
                alert('You are not allowed to delete this pic !');
            }
        })
    }

    function _handleAlbum() {
        axios.post(URL + 'picture', {
            user: acc.user
        })
        .then(res => {
            dispatch(album_arr(res.data));
            dispatch(ui_nav(2));
        });
    }

    function _like(e) {
        if (acc.user !== '') {
            axios.post(URL + 'like', {
                id: acc.user,
                pic: e
            })
            .then(res => {
                _handleAlbum();
            })
        }
        else {
            alert('Log in first !');
            dispatch(ui_nav(3));
        }
    }

    function _comment(e) {
        axios.post(URL + 'view_comment', {
            pic: e
        }).then(res => {
            _handleAlbum();
        })
    }

    function Comment_push(e) {
        if (acc.user === '') {
            alert('Log in first !');
            dispatch(ui_nav(3));
        } else if (document.getElementById('post_comment').textContent === '') {
            console.log('Nothing')
        } else {
            axios.post(URL + 'comment_push', {
                text: document.getElementById('post_comment').textContent,
                user: acc.user,
                pic: e
            })
        }
    }

    function _displayComment(e) {
        let temp = [];

        axios.post(URL + 'display_comment', {
            pic: e
        })
        .then(res => {
            for (let i = 0; i < res.data.length; i++) {
                temp[i] = res.data[i].name + ' : ' + res.data[i].text;
            }
            dispatch(album_comment(temp));
        })
    }

    return (
        <div className="body-album">
            <div className="body-album-part">
                { album.arr.map((each, index) =>
                    <div className="body-album-each" key={index} >
                        <div className="body-album-pic" style={{ backgroundImage: 'url(\'data:image/jpeg;base64,' + each.temp + '\')' }} />
                        {each.is_like === null ? <GoHeart className="body-album-ft-1" onClick={() => _like(each.pic)} /> : <GoHeart className="body-album-ft-1-1" onClick={() => _like(each.pic)} />}
                        {each.display === 0 ? <GoComment className="body-album-ft-2" onClick={() => _comment(each.pic)} /> : <GoComment className="body-album-ft-2-1" onClick={() => _comment(each.pic)} />}
                        <div className="body-album-postid">{each.name}</div>
                        <IoIosCloseCircleOutline className="body-album-ft2" onClick={() => One_del(each.pic)} />
                        <div className="count_like">{each.count} Likes</div>
                        <div className="count_comment">{each.count_text} Comments</div>
                        {each.display === 0 ? <div className="body-album-comment">{each.explain}</div> : ''}
                        {each.display === 1 && each.count_text === 0 ? <div className="body-album-comment">No comments yet</div> : ''}
                        {each.display === 1 && each.count_text !== 0 ?  
                            <div className="body-album-comment-1">
                                {_displayComment(each.pic)}
                                <div className="ba-comment-top">{album.comment.map(x => x + '<br>')}</div>
                                <div type="text" className="ba-comment-btm" contentEditable="true" id="post_comment" />
                                <IoIosAddCircleOutline className="ba-comment-send" onClick={() => Comment_push(each.pic)}/>
                            </div> : ''
                        }
                    </div>
                )} 
            </div>
        </div>
    );
}

export default BodyAlbum;
