const album = {
    arr: [],
    comment: []
}

const Reducer_album = (state = album, action) => {
    switch (action.type) {
        case 'ALBUM_ARR':
            return Object.assign({}, state, {
                arr: action.payload
            });
        case 'ALBUM_COMMENT':
            return Object.assign({}, state, {
                comment: action.payload
            });
        default:
            return state;
    }
}

export default Reducer_album;
