import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { ui_nav, album_arr, cam_arr, cam_curr } from '../../actions';

import axios from 'axios';
import { URL } from '../../const';

import './index.css';

function Header() {
    const dispatch = useDispatch();
    let acc = useSelector(state => state.acc);

    function _handleAlbum() {
        axios.post(URL + 'picture', {
            user: acc.user
        })
        .then(res => {
            var arr = res.data;
            async function test(arr) {
                for(const item in arr) {
                    await axios.post(URL + 'display_comment', {
                        pic: item.pic
                    })
                    .then(res2 => {
                        console.log(res2);
                    });
                }    
                console.log('done');
            }

            // for(var i = 0; i < res.data.length; i++) {
            //     axios.post(URL + 'display_comment', {
            //         pic: res.data.pic
            //     })
            //     .then(res2 => {
            //         console.log(i);
                    // let tmp = [];
                    // let rr = [];

                    // for (let i = 0; i < res.data.length; i++) {
                    //     tmp[i] = res.data[i].name + ' : ' + res.data[i].text;
                    // }
                    // console.log(tmp);
                    // rr = album.arr;
                    // rr[index] = (Object.assign({tmp}, album.arr[index]))
                    // dispatch(album_arr(rr));
            //     })
            // }




            // dispatch(album_arr(res.data));
            // dispatch(ui_nav(2));
        });
    }

    function _handle_cam() {
        dispatch(cam_arr([]));
        dispatch(ui_nav(1));
        dispatch(cam_curr(-1));
    }

    function _albumSet() {
        axios.post(URL + 'albumSet', {})
        .then(res => {
            _handleAlbum();
        })
    }

	return (
		<div className="header">
            <div className="header-top">
                <div className="header-home menu" onClick={() => dispatch(ui_nav(0))}>HOME</div>
                <div className="header-camera menu" onClick={() => _handle_cam()}>CAMERA</div>
                <div className="header-album menu" onClick={() => _albumSet()}>ALBUM</div>
                {acc.signin === 0 ? <div className="header-account menu" onClick={() => dispatch(ui_nav(3))}>ACCOUNT</div> :
            <div className="header-account menu" onClick={() => dispatch(ui_nav(3))}>USER_INFO</div>}
            </div>
            <div className="header-bottom" />
        </div>
	);
}

export default Header;
